changeSet.sql:
update crm_provider_contact a set address_country =(select description_en from adm_country c where 
			lower(c.country_code)=lower(a.address_country) or lower(c.description_en)=lower(a.address_country))
			where a.address_country in (select c.country_code from adm_country c);

update account_entity a set address_country =(select description_en from adm_country c where 
			lower(c.country_code)=lower(a.address_country) or lower(c.description_en)=lower(a.address_country)) 
			where a.address_country in (select c.country_code from adm_country c);
