con:testCase.con:setupScript:
import com.eviware.soapui.impl.wsdl.teststeps.*
import com.eviware.soapui.support.types.*;

StringToStringMap headers = new StringToStringMap();
StringToStringMap headersForSuper = new StringToStringMap();

String supeUserPass = testCase.testSuite.project.getPropertyValue("meveoSuperUsername")+":"+testCase.testSuite.project.getPropertyValue("meveoSuperPassword");
 log.info("userPass="+supeUserPass);

String userPass = testCase.testSuite.project.getPropertyValue("meveoUsername")+":"+testCase.testSuite.project.getPropertyValue("meveoPassword");
 log.info("userPass="+userPass);
headers.put("Authorization","Basic "+userPass.getBytes().encodeBase64());
headersForSuper.put("Authorization","Basic "+supeUserPass.getBytes().encodeBase64());
for(testSuiteItem in  testCase.testSuite.project.getTestSuiteList()){
	for( testCaseItem in testSuiteItem.getTestCaseList() ) {
	    log.info("Setting basic auth for all WSDL test requests in test case ["+testCaseItem.getName()+"]")
	    for( testStep in testCaseItem.getTestStepList() ) {

	          if (!(testStep instanceof WsdlTestRequestStep || testStep instanceof RestTestRequestStep )){
	          	continue;
	          }

	    		testStep.getTestRequest().setPreemptive(true);


			if(testStep.getName().contains("AUTHORIZATION_MANUAL")) {
				testStep.getTestRequest().setRequestHeaders(new StringToStringMap());

		    	} else if (testStep.getName().contains("AUTHORIZATION_ADMIN")) {
		    		testStep.getTestRequest().setRequestHeaders(headers);

		    	} else if(testCaseItem.getName().contains("AUTHORIZATION_SUPERADMIN") 
		    	    || testStep.getName().contains("AUTHORIZATION_SUPERADMIN")){
		    		testStep.getTestRequest().setRequestHeaders(headersForSuper);

		    	} else if(!testStep.getName().contains("AUTHORIZATION_MANUAL")) {
				testStep.getTestRequest().setRequestHeaders(headers);
		    	}
	    }
	}
}

